mapper.select:
select A.id,A.user_type,A.mobile,A.password,A.nick_name,A.name,A.id_card,A.email,A.openid,A.head_img,A.bind_status,A.borrow_auth_status,A.bind_code,A.integral,A.status
from user_info A
where
    A.mobile = #{mobile}
and A.password = #{password}
and A.user_type = #{userType}

mapper.update:
update user_info
    set status = #{status}
where user_info.id = #{id}

mapper.select:
select A.id,A.user_type,A.mobile,A.name,A.id_card,A.integral,A.create_time,A.bind_code,A.status
from user_info A
where A.user_type = #{userType}
and A.mobile = #{monile}
and A.status = #{status}
